            do {
                jsonEncodedContacts += "{";
                for(int idx=0; idx < cursor.getColumnCount(); idx++){
                    jsonEncodedContacts += " \"" + cursor.getColumnName(idx) + "\":\"" + cursor.getString(idx) + "\",";
